mapper.select:
  		select * from t_spu t
   			 
		        
		          and  t.reserved1 like "%"#{reserved1}"%"
		        
		        
		          and t.status =#{status}
		        
		        
		          and  t.product_num LIKE CONCAT(CONCAT('%',#{product_num}),'%')
		        
		        
		          and  t.spu_name LIKE CONCAT(CONCAT('%',#{spu_name}),'%')
		        
             limit #{start},#{end}
